import jsonwebtoken from "jsonwebtoken";
export function getSignedJsonWebToken({ id, privateKey }) {
    const now = Math.floor(Date.now() / 1000);
    const payload = {
        iat: now,
        exp: now + 60 * 10 - 30,
        iss: id
    };
    const token = jsonwebtoken.sign(payload, privateKey, { algorithm: "RS256" });
    return token;
}
